import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import OwlCarousel from "react-owl-carousel2";

const options = {
  items: 1,
  dots: false,
  nav: true,
  navText: [
    '<span class="i la la-long-arrow-left"></span>',
    '<span class="i la la-long-arrow-right"></span>',
  ],
  navElement: "button",
};

class Testimonial extends Component {
  render() {
    const { tistimonial } = this.props;
    return (
      <Fragment>
        <OwlCarousel
          options={options}
          className="testimonial-carousel owl-carousel"
        >
          {Object.values(tistimonial)
            .slice(0, 2)
            .map((value, key) => {
              const { img } = value;
              return (
                <div className="carousel-single" key={key}>
                  <div className="author-thumb">
                    <img src={img} alt="" className="rounded-circle" />
                  </div>
                  <div className="author-info">
                    <h4>Vaibhav Gupta</h4>
                    <span>Toronto, Canada</span>
                  </div>
                  <p className="author-comment">
                    Excepteur sint occaecat cupidatat non proident sunt in culpa
                    officia deserunt mollit anim laborum sint occaecat cupidatat
                    non proident. Occaecat cupidatat non proident culpa officia
                    deserunt mollit.
                  </p>
                </div>
              );
            })}
        </OwlCarousel>
      </Fragment>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    tistimonial: state.testimonial,
  };
};
export default connect(mapStateToProps)(Testimonial);
